import { WhatsAppChannelInterface } from '@vonage/messages/dist/interfaces/WhatsApp/WhatsAppChannelInterface';
import { MessageInterface } from '@vonage/messages/dist/interfaces/MessageInterface';
import { WhatsAppStickerUrlType } from '@vonage/messages/dist/types';
import { WhatsAppStickerIdType } from '@vonage/messages/dist/types';
/**
 * Represents an interface for WhatsApp sticker messages.
 *
 * @deprecated Please use types instead of interfaces.
 *
 * @ignore
 */
export interface WhatsAppStickerInterface extends WhatsAppChannelInterface, MessageInterface {
    /**
     * Specifies the type of message, which is "sticker" for sticker messages.
     */
    messageType: 'sticker';
    /**
     * The sticker to be sent. It can be either a sticker URL or a sticker ID.
     */
    sticker: WhatsAppStickerUrlType | WhatsAppStickerIdType;
}
