import { MMSAudioParams } from '@vonage/messages/dist/types/Channels/MMS/MMSAudioParams';
import { MMSImageParams } from '@vonage/messages/dist/types/Channels/MMS/MMSImageParams';
import { MMSVcardParams } from '@vonage/messages/dist/types/Channels/MMS/MMSVcardParams';
import { MMSVideoParams } from '@vonage/messages/dist/types/Channels/MMS/MMSVideoParams';
import { Channels } from "@vonage/messages/dist/enums";
export * from '@vonage/messages/dist/types/Channels/MMS/MMSAudioParams';
export * from '@vonage/messages/dist/types/Channels/MMS/MMSImageParams';
export * from '@vonage/messages/dist/types/Channels/MMS/MMSVcardParams';
export * from '@vonage/messages/dist/types/Channels/MMS/MMSVideoParams';
/**
 * Represents a union type that can be any of the MMS-specific message parameters.
 *
 * @group MMS
 * @category Parameters
 */
export type AnyMMSParams = MMSAudioParams | MMSImageParams | MMSVcardParams | MMSVideoParams;
/**
 * Represents a union type that includes the 'channel' property set to 'mms'
 * along with any of the MMS-specific message parameters.
 *
 * @group MMS
 */
export type AnyMMSChannel = {
    /**
     * The channel through which the message will be sent, which is 'mms' for MMS.
     */
    channel: Channels.MMS;
} & AnyMMSParams;
