import { MessengerAudioParams } from '@vonage/messages/dist/types/Channels/Messenger/MessengerAudioParams';
import { MessengerFileParams } from '@vonage/messages/dist/types/Channels/Messenger/MessengerFileParams';
import { MessengerImageParams } from '@vonage/messages/dist/types/Channels/Messenger/MessengerImageParams';
import { MessengerParams } from '@vonage/messages/dist/types/Channels/Messenger/MessengerParams';
import { MessengerTextParams } from '@vonage/messages/dist/types/Channels/Messenger/MessengerTextParams';
import { MessengerType } from '@vonage/messages/dist/types/Channels/Messenger/MessengerType';
import { MessengerVideoParams } from '@vonage/messages/dist/types/Channels/Messenger/MessengerVideoParams';
import { Channels } from "@vonage/messages/dist/enums";
export * from '@vonage/messages/dist/types/Channels/Messenger/MessengerAudioParams';
export * from '@vonage/messages/dist/types/Channels/Messenger/MessengerFileParams';
export * from '@vonage/messages/dist/types/Channels/Messenger/MessengerImageParams';
export * from '@vonage/messages/dist/types/Channels/Messenger/MessengerParams';
export * from '@vonage/messages/dist/types/Channels/Messenger/MessengerTextParams';
export * from '@vonage/messages/dist/types/Channels/Messenger/MessengerType';
export * from '@vonage/messages/dist/types/Channels/Messenger/MessengerVideoParams';
/**
 * Represents a union type that can be any of the Messenger-specific message
 * parameters or MessengerType.
 *
 * @group Messenger
 * @category Parameters
 */
export type AnyMessengerParams = MessengerAudioParams | MessengerFileParams | MessengerImageParams | MessengerParams | MessengerTextParams | MessengerType | MessengerVideoParams;
/**
 * Represents a union type that includes the 'channel' property set to 'messenger'
 * along with any of the Messenger-specific message parameters or MessengerType.
 */
export type AnyMessengerChannel = {
    /**
     * The channel through which the message will be sent, which is 'messenger' for Messenger.
     */
    channel: Channels.MESSENGER;
} & AnyMessengerParams;
