import { PhoneEndpoint } from '@vonage/voice/dist/types/Endpoint/PhoneEndpoint';
import { SIPEndpoint } from '@vonage/voice/dist/types/Endpoint/SIPEndpoint';
import { VBCEndpoint } from '@vonage/voice/dist/types/Endpoint/VBCEndpoint';
import { WebsocketEndpoint } from '@vonage/voice/dist/types/Endpoint/WebsocketEndpoint';
/**
 * Represents a call endpoint, which can be of different types such as Phone, SIP, VBC, or Websocket.
 * This type can be used to specify the destination for an outbound call.
 */
export type CallEndpoint = WebsocketEndpoint | PhoneEndpoint | SIPEndpoint | VBCEndpoint;
